package com.rongke.utils.ans;//import com.core.smarttool.util.file.SaveFileUtil;import org.springframework.util.ResourceUtils;import javax.servlet.http.HttpServletRequest;import java.io.File;public class FileUtil {//    public static void uploadFile(byte[] bytes, String filePath) throws Exception {//        boolean save = SaveFileUtil.smartToolSaveFile(filePath, bytes);//        System.out.println("保存结果:" + (save ? "成功" : "失败"));//    }    public static String certificate() {        try {            String rootPath = ResourceUtils.getURL("classpath:").getPath();            String certFileName = "apiclient_cert.p12"; // resource/cert/wechat 目录下的文件名            String diskPath = rootPath + "cert" + File.separator + "wechat" + File.separator + certFileName;            return diskPath;        } catch (Exception e) {            return "";        }    }    public static String getProjectBaseUrlNoProject(HttpServletRequest req){        String scheme=req.getHeader("X-Forwarded-Scheme");        if(scheme==null || scheme.equals("")){            scheme=req.getScheme();        }        String port="";        if(req.getServerPort()!=80 && req.getServerPort()!=443){            port = ":"+req.getServerPort();        }        String XM = "/";        if(!req.getContextPath().equals("/")){            XM = req.getContextPath()+"/";        }        String basePath=req.getScheme() + "://"                + req.getServerName() +port+ XM;        return basePath;    }}