package com.rongke.yinshengbao.entity;//商户账户余额查询接口public class BalanceQuery {	private String accountId;	private String mac;	private String key;	/**	 * 商户编号	 * @return the accountId	 */	public String getAccountId() {		return accountId;	}	/**	 * 商户编号	 * @param accountId the accountId to set	 */	public void setAccountId(String accountId) {		this.accountId = accountId;	}	/**	 * 数字签名	 * @return the mac	 */	public String getMac() {				return mac;	}	/**	 * 数字签名	 * @param mac the mac to set	 */	public void setMac(String mac) {		this.mac = mac;	}		/**	 * @return the key	 */	public String getKey() {		return key;	}	/**	 * @param key the key to set	 */	public void setKey(String key) {		this.key = key;	}	/* (non-Javadoc)	 * @see java.lang.Object#toString()	 */	@Override	public String toString() {		return "accountId=" + accountId +"&key="+key;	}	}