package com.rongke.yinshengbao.entity;//商户账户余额查询接口public class BalanceQueryRet {	private String result_code;	private String result_msg;	private String balance;	/**	 * 返回码 0000表示查询操作成功	 * @return the result_code	 */	public String getResult_code() {		return result_code;	}	/**	 * 返回码 0000表示查询操作成功	 * @param result_code the result_code to set	 */	public void setResult_code(String result_code) {		this.result_code = result_code;	}	/**	 * 错误信息	 * @return the result_msg	 */	public String getResult_msg() {		return result_msg;	}	/**	 * 错误信息	 * @param result_msg the result_msg to set	 */	public void setResult_msg(String result_msg) {		this.result_msg = result_msg;	}	/**	 * 账户余额，返回码为0000时返回	 * @return the balance	 */	public String getBalance() {		return balance;	}	/**	 * 账户余额，返回码为0000时返回	 * @param balance the balance to set	 */	public void setBalance(String balance) {		this.balance = balance;	}	}