package com.rongke.yinshengbao.entity;//结果通知public class ResultInfo {	private String result_code;	private String result_msg;	private String amount;	private String orderId;	private String mac;	/**	 * 响应码	 * @return the result_code	 */	public String getResult_code() {		return result_code;	}	/**	 * 响应码	 * @param result_code the result_code to set	 */	public void setResult_code(String result_code) {		this.result_code = result_code;	}	/**	 * 返回信息	 * @return the result_msg	 */	public String getResult_msg() {		return result_msg;	}	/**	 * 返回信息	 * @param result_msg the result_msg to set	 */	public void setResult_msg(String result_msg) {		this.result_msg = result_msg;	}	/**	 * 金额	 * @return the amount	 */	public String getAmount() {		return amount;	}	/**	 * 金额	 * @param amount the amount to set	 */	public void setAmount(String amount) {		this.amount = amount;	}	/**	 * 订单号	 * @return the orderId	 */	public String getOrderId() {		return orderId;	}	/**	 * 订单号	 * @param orderId the orderId to set	 */	public void setOrderId(String orderId) {		this.orderId = orderId;	}	/**	 * 数字签名	 * @return the mac	 */	public String getMac() {		return mac;	}	/**	 * 数字签名	 * @param mac the mac to set	 */	public void setMac(String mac) {		this.mac = mac;	}	}