package com.rongke.enums;import org.apache.commons.lang3.StringUtils;/** * Created by bilibili on 2017/12/26. *///一个用户只有一个最新的状态。public enum NewestStatusEnum {//ing表示 执行中    AccountPendingAuth(0,"待认证"),//已注册，待认证    AccountAuthing(1,"认证中"),//开始认证，认证中，第一步认证开始    AccountAuthFailed(2,"认证失败"),    LoanPendingApply(3,"贷款待申请"), //认证完毕待申请    LoanAppling(4,"贷款申请中"),//用户提交贷款申请的时候    PendingMachineAudit(5,"贷款待机审"),//已经申请，待机审    MachineAuditFailed(6,"贷款机审拒绝"),//拒绝    PendingHumanAudit(7,"贷款待人工审核"),//已经机审待人工审核    HumanFailed(8,"贷款人工审核拒绝"),//拒绝    PendingRemit(9,"待打款"),//审核通过，待打款    RemitRefused(10,"打款拒绝"),//拒绝    RemitFailed(11,"打款失败"),//通过回调确定打款失败    AleardyPayInterest(12,"已付息"),//已打款付息，通过回调确定    AleardyRenewInterest(13,"已续期"),//已续期    PendingPayInterest(14,"待付息"),//到达还款周期（默认七天）状态为待付息    OverduePayInterest(15,"已逾期"),//过了还款周期(默认七天)状态为已逾期    LoanOver(16,"已完结"),//申请的贷款数额全部还清    AccountBlacklist(17,"黑名单"),//账号被列入黑名单，不能申请贷款    AccountForbidden(18,"禁用");//账号被禁用，不能登入    private Integer type;    private String typeName;    NewestStatusEnum(Integer type, String typeName) {        this.type = type;        this.typeName = typeName;    }    public static NewestStatusEnum newestStatusEnumTypeName(Integer type){        for(NewestStatusEnum newestStatusEnum : NewestStatusEnum.values()){            if(StringUtils.equals(type.toString(),newestStatusEnum.getType().toString())){                return newestStatusEnum;            }        }        return null;    }    public Integer getType() {        return type;    }    public void setType(Integer type) {        this.type = type;    }    public String getTypeName() {        return typeName;    }    public void setTypeName(String typeName) {        this.typeName = typeName;    }}