package com.rongke.enums;/** * Created by bilibili on 2017/12/1. */public enum QuestionEnum {    BASICMSG(1,"基本信息认证"),IDENTITY(2,"身份证认证"),BANKCARD(3,"银行卡认证"),PHONERECORD(4,"运营商认证"),    TAOBAO(5,"淘宝认证"),SCHOOLINFO(6,"学信网认证");    private Integer type;    private String typeName;    QuestionEnum(Integer type, String typeName) {        this.type = type;        this.typeName = typeName;    }    /**     * 根据type 获取typeName     * @param type     * @return     */    public static QuestionEnum getQuestionByType(Integer type) {        for (QuestionEnum userTypeEnum: values()) {            if (userTypeEnum.getType().equals(type)) {                return userTypeEnum;            }        }        return null;    }    public Integer getType() {        return type;    }    public void setType(Integer type) {        this.type = type;    }    public String getTypeName() {        return typeName;    }    public void setTypeName(String typeName) {        this.typeName = typeName;    }}