package com.rongke.enums;/** * Created by bilibili on 2017/11/27. */public enum UserStatusEnum {    QUALIFIED(1,"正常"),NOTQUALIFIED(2,"黑名单"),DELAY(3,"推迟"),INELIGIBLE(4,"无资格"),DISABLE(5,"禁用");    private Integer type;    private String typeName;    UserStatusEnum(Integer type, String typeName) {        this.type = type;        this.typeName = typeName;    }    /**     * 根据type 获取typeName     * @param type     * @return     */    public static UserStatusEnum getUserTypeByType(Integer type) {        for (UserStatusEnum userTypeEnum: values()) {            if (userTypeEnum.getType().equals(type)) {                return userTypeEnum;            }        }        return null;    }    public Integer getType() {        return type;    }    public void setType(Integer type) {        this.type = type;    }    public String getTypeName() {        return typeName;    }    public void setTypeName(String typeName) {        this.typeName = typeName;    }}