package com.rongke.model;import com.fasterxml.jackson.annotation.JsonFormat;import com.fasterxml.jackson.databind.annotation.JsonDeserialize;import com.fasterxml.jackson.databind.annotation.JsonSerialize;import com.rongke.utils.DateJsonDeserializer;import com.rongke.utils.DateJsonSerializer;import org.springframework.format.annotation.DateTimeFormat;import com.baomidou.mybatisplus.annotations.TableField;import com.baomidou.mybatisplus.annotations.TableId;import com.baomidou.mybatisplus.annotations.TableName;import com.baomidou.mybatisplus.enums.IdType;import java.io.Serializable;/** * @HelpCenter * @(help_center) * @version : Ver 1.0 */@TableName("help_center")public class HelpCenter implements Serializable {	@TableField(exist = false)	private static final long serialVersionUID = 1L;	/**	 * @备注:主键id	 * @字段:id BIGINT(19)	 */	@TableId(type = IdType.AUTO)	@JsonFormat(shape = JsonFormat.Shape.STRING)	private java.lang.Long id;	/**	 * @备注:创建时间	 * @字段:gmt_datetime DATETIME(19)	 */	@JsonSerialize(using=DateJsonSerializer.class)	@JsonDeserialize(using=DateJsonDeserializer.class)	@DateTimeFormat(pattern = "yyyy-MM-dd HH:mm:ss" )	private java.util.Date gmtDatetime;	/**	 * @备注:修改时间	 * @字段:upt_datetime DATETIME(19)	 */	@JsonSerialize(using=DateJsonSerializer.class)	@JsonDeserialize(using=DateJsonDeserializer.class)	@DateTimeFormat(pattern = "yyyy-MM-dd HH:mm:ss" )	private java.util.Date uptDatetime ;	/**	 * @备注:帮助内容	 * @字段:content TEXT(65535)	 */	private java.lang.String content;	/**	 * @备注:帮助标题	 * @字段:title VARCHAR(45)	 */	private java.lang.String title;	/**	 * @备注:类型 1,借款问题 2,还款问题 3,其他问题	 * @字段:type VARCHAR(45)	 */	private java.lang.String type;	/**	 * @备注:状态	 * @字段:status VARCHAR(45)	 */	private java.lang.String status;	public HelpCenter(){	}	public HelpCenter(		java.lang.Long id	){		this.id = id;	}	public void setId(java.lang.Long id) {		this.id = id;	}	public java.lang.Long getId() {		return this.id;	}	public void setGmtDatetime(java.util.Date gmtDatetime) {		this.gmtDatetime = gmtDatetime;	}	public java.util.Date getGmtDatetime() {		return this.gmtDatetime;	}	public void setUptDatetime(java.util.Date uptDatetime) {		this.uptDatetime = uptDatetime;	}	public java.util.Date getUptDatetime() {		return this.uptDatetime;	}	public void setContent(java.lang.String content) {		this.content = content;	}	public java.lang.String getContent() {		return this.content;	}	public void setTitle(java.lang.String title) {		this.title = title;	}	public java.lang.String getTitle() {		return this.title;	}	public void setType(java.lang.String type) {		this.type = type;	}	public java.lang.String getType() {		return this.type;	}	public String getStatus() {		return status;	}	public void setStatus(String status) {		this.status = status;	}}