package com.rongke.model.ans;import com.baomidou.mybatisplus.annotations.TableField;import com.baomidou.mybatisplus.annotations.TableId;import com.baomidou.mybatisplus.annotations.TableName;import com.baomidou.mybatisplus.enums.FieldFill;import com.baomidou.mybatisplus.enums.IdType;import com.fasterxml.jackson.annotation.JsonFormat;import java.io.Serializable;import java.util.Date;/** * 微信  富文本 *  * @author Ans * @email 867917319@qq.com * @date 2018-08-22 15:48:29 */@TableName("wx_about")public class WxAboutEntity implements Serializable {	private static final long serialVersionUID = 1L;	/**	 * 富文本  ID	 */	@TableId(type = IdType.AUTO)	private Long id;	/**	 * 平台内容	 */	private String title;	/**	 * 详情	 */	private String content;	/**	 * 类型 1 关于	 */	private Integer type;	/**	 * 描述	 */	private String typeRemark;	/**	 * 创建时间	 */	@TableField(value = "create_time",fill = FieldFill.INSERT)	@JsonFormat(pattern = "yyyy-MM-dd HH:mm:ss",timezone = "GMT+8")	private Date createTime;	/**	 * 修改时间	 */	@TableField(value = "update_time",fill = FieldFill.INSERT_UPDATE)	@JsonFormat(pattern = "yyyy-MM-dd HH:mm:ss",timezone = "GMT+8")	private Date updateTime;	/**	 * 状态	 */	private String status;	/**	 * 备注	 */	private String memo;	/**	 * 设置：富文本  ID	 */	public void setId(Long id) {		this.id = id;	}	/**	 * 获取：富文本  ID	 */	public Long getId() {		return id;	}	/**	 * 设置：平台内容	 */	public void setTitle(String title) {		this.title = title;	}	/**	 * 获取：平台内容	 */	public String getTitle() {		return title;	}	/**	 * 设置：详情	 */	public void setContent(String content) {		this.content = content;	}	/**	 * 获取：详情	 */	public String getContent() {		return content;	}	/**data	 * 设置：类型	 */	public void setType(Integer type) {		this.type = type;	}	/**	 * 获取：类型	 */	public Integer getType() {		return type;	}	/**	 * 设置：描述	 */	public void setTypeRemark(String typeRemark) {		this.typeRemark = typeRemark;	}	/**	 * 获取：描述	 */	public String getTypeRemark() {		return typeRemark;	}	/**	 * 设置：创建时间	 */	public void setCreateTime(Date createTime) {		this.createTime = createTime;	}	/**	 * 获取：创建时间	 */	public Date getCreateTime() {		return createTime;	}	/**	 * 设置：修改时间	 */	public void setUpdateTime(Date updateTime) {		this.updateTime = updateTime;	}	/**	 * 获取：修改时间	 */	public Date getUpdateTime() {		return updateTime;	}	/**	 * 设置：状态	 */	public void setStatus(String status) {		this.status = status;	}	/**	 * 获取：状态	 */	public String getStatus() {		return status;	}	/**	 * 设置：备注	 */	public void setMemo(String memo) {		this.memo = memo;	}	/**	 * 获取：备注	 */	public String getMemo() {		return memo;	}}