package com.rongke.service.impl;import com.baomidou.mybatisplus.mapper.EntityWrapper;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.AddressMapper;import com.rongke.model.Address;import com.rongke.service.AddressService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @AddressServiceImpl * @地址ServiceImpl * @version : Ver 1.0 */@Servicepublic class AddressServiceImpl extends ServiceImpl<AddressMapper, Address> implements AddressService {    @Autowired    private AddressMapper addressMapper;    /**     * @根据条件查找地址     * @param address     * @return     */    @Override    public List<Address> selectByCondition(Address address) {        return addressMapper.selectByCondition(address);    }}