package com.rongke.service.impl;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.AdminPermissionMapper;import com.rongke.model.AdminPermission;import com.rongke.service.AdminPermissionService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @AdminPermissionServiceImpl * @ServiceImpl * @version : Ver 1.0 */@Servicepublic class AdminPermissionServiceImpl extends ServiceImpl<AdminPermissionMapper, AdminPermission> implements AdminPermissionService {    @Autowired    private AdminPermissionMapper adminPermissionMapper;    /**     * @根据条件查找     * @param adminPermission     * @return     */    @Override    public List<AdminPermission> selectByCondition(AdminPermission adminPermission) {        return adminPermissionMapper.selectByCondition(adminPermission);    }    @Override    public List<Integer> selectMenus(Integer roleId) {        return adminPermissionMapper.selectMenus(roleId);    }}