package com.rongke.service.impl;import com.baomidou.mybatisplus.mapper.EntityWrapper;import com.baomidou.mybatisplus.plugins.Page;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.AdminRoleMapper;import com.rongke.model.AdminRole;import com.rongke.service.AdminRoleService;import com.rongke.utils.ans.PageUtils;import com.rongke.utils.ans.Query;import com.rongke.utils.ans.R;import org.apache.commons.lang3.StringUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;/** * @AdminRoleServiceImpl * @ServiceImpl * @version : Ver 1.0 */@Servicepublic class AdminRoleServiceImpl extends ServiceImpl<AdminRoleMapper, AdminRole> implements AdminRoleService {    @Autowired    private AdminRoleMapper adminRoleMapper;    /**     * @根据条件查找     * @param adminRole     * @return     */    @Override    public List<AdminRole> selectByCondition(AdminRole adminRole) {        return adminRoleMapper.selectByCondition(adminRole);    }    /**     * 分页获取所有角色     */    public PageUtils rolePage(Map<String, Object> params) {        System.out.println((String)params.get("name"));        Page<AdminRole> adminRolePage = this.selectPage(new Query<AdminRole>(params).getPage(), new EntityWrapper<AdminRole>()                .like(StringUtils.isNotBlank((String)params.get("name")),"name",(String)params.get("name"))                .eq(StringUtils.isNotBlank((String)params.get("type")),"type", params.get("type"))        );        return new PageUtils(adminRolePage);    }}