package com.rongke.service.impl;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.BaiqishiMapper;import com.rongke.model.Baiqishi;import com.rongke.service.BaiqishiService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @BaiqishiServiceImpl * @ServiceImpl * @version : Ver 1.0 */@Servicepublic class BaiqishiServiceImpl extends ServiceImpl<BaiqishiMapper, Baiqishi> implements BaiqishiService {    @Autowired    private BaiqishiMapper baiqishiMapper;    /**     * @根据条件查找     * @param baiqishi     * @return     */    @Override    public List<Baiqishi> selectByCondition(Baiqishi baiqishi) {        return baiqishiMapper.selectByCondition(baiqishi);    }}