package com.rongke.service.impl;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.BankCardMapper;import com.rongke.model.BankCard;import com.rongke.service.BankCardService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;/** * @BankCardServiceImpl * @用户银行卡ServiceImpl * @version : Ver 1.0 */@Servicepublic class BankCardServiceImpl extends ServiceImpl<BankCardMapper, BankCard> implements BankCardService {    @Autowired    private BankCardMapper bankCardMapper;    /**     * @根据条件查找用户银行卡     * @param bankCard     * @return     */    @Override    public List<BankCard> selectByCondition(BankCard bankCard) {        return bankCardMapper.selectByCondition(bankCard);    }    @Override    public Integer selectCount(Map map) {        return bankCardMapper.selectCount(map);    }    @Override    public List<BankCard> selectByPage(Map map) {        return bankCardMapper.selectByPage(map);    }}