package com.rongke.service.impl;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.ClassifyMapper;import com.rongke.model.Classify;import com.rongke.service.ClassifyService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @ClassifyServiceImpl * @分类ServiceImpl * @version : Ver 1.0 */@Servicepublic class ClassifyServiceImpl extends ServiceImpl<ClassifyMapper, Classify> implements ClassifyService {    @Autowired    private ClassifyMapper classifyMapper;    /**     * @根据条件查找分类     * @param classify     * @return     */    @Override    public List<Classify> selectByCondition(Classify classify) {        return classifyMapper.selectByCondition(classify);    }}