package com.rongke.service.impl;import com.baomidou.mybatisplus.mapper.EntityWrapper;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.ConstantMapper;import com.rongke.model.TConstant;import com.rongke.service.ConstantService;import org.springframework.stereotype.Service;/** * 系统配置 */@Service("constantService")public class ConstantServiceImpl extends ServiceImpl<ConstantMapper, TConstant> implements ConstantService {    public TConstant getConstant(){        TConstant tConstant = this.selectOne(new EntityWrapper<TConstant>());        return tConstant;    }}