package com.rongke.service.impl;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.CouponMapper;import com.rongke.model.Coupon;import com.rongke.service.CouponService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @CouponServiceImpl * @优惠卷ServiceImpl * @version : Ver 1.0 */@Servicepublic class CouponServiceImpl extends ServiceImpl<CouponMapper, Coupon> implements CouponService {    @Autowired    private CouponMapper couponMapper;    /**     * @根据条件查找优惠卷     * @param coupon     * @return     */    @Override    public List<Coupon> selectByCondition(Coupon coupon) {        return couponMapper.selectByCondition(coupon);    }}