package com.rongke.service.impl;import com.baomidou.mybatisplus.mapper.EntityWrapper;import com.baomidou.mybatisplus.plugins.Page;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.DecisionFieldMapper;import com.rongke.model.DecisionField;import com.rongke.service.DecisionFieldService;import com.rongke.utils.ans.PageUtils;import com.rongke.utils.ans.Query;import org.springframework.stereotype.Service;import java.util.*;/** * @UserStatusServiceImpl * @ServiceImpl * @version : Ver 1.0 */@Servicepublic class DecisionFieldServiceImpl extends ServiceImpl<DecisionFieldMapper, DecisionField> implements DecisionFieldService {    @Override    public PageUtils getPages(Map<String,Object> params) {        Page<DecisionField> page = this.selectPage(new Query<DecisionField>(params).getPage(), new EntityWrapper<DecisionField>()                .orderDesc(Arrays.asList("type"))        );        return new PageUtils(page);    }}