package com.rongke.service.impl;import com.baomidou.mybatisplus.mapper.EntityWrapper;import com.baomidou.mybatisplus.plugins.Page;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.DecisionRuleSetMapper;import com.rongke.model.DecisionRuleSet;import com.rongke.service.DecisionRuleSetService;import com.rongke.utils.ans.PageUtils;import com.rongke.utils.ans.Query;import org.springframework.stereotype.Service;import java.util.Arrays;import java.util.List;import java.util.Map;/** * @UserStatusServiceImpl * @ServiceImpl * @version : Ver 1.0 */@Servicepublic class DecisionRuleSetServiceImpl extends ServiceImpl<DecisionRuleSetMapper, DecisionRuleSet> implements DecisionRuleSetService {    @Override    public PageUtils getPages(Map<String, Object> params) {        Page<DecisionRuleSet> page = this.selectPage(new Query<DecisionRuleSet>(params).getPage(), new EntityWrapper<DecisionRuleSet>()                .orderDesc(Arrays.asList("version"))        );        return new PageUtils(page);    }    @Override    public List<DecisionRuleSet> getDecisionRuleSets()    {        return this.selectList(new EntityWrapper<DecisionRuleSet>().eq("is_use",1));    }}