package com.rongke.service.impl;import com.baomidou.mybatisplus.mapper.EntityWrapper;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.DictionaryDataMapper;import com.rongke.model.DictionaryData;import com.rongke.service.DictionaryDataService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @DictionaryDataServiceImpl * @ServiceImpl * @version : Ver 1.0 */@Servicepublic class DictionaryDataServiceImpl extends ServiceImpl<DictionaryDataMapper, DictionaryData> implements DictionaryDataService {    @Autowired    private DictionaryDataMapper dictionaryDataMapper;    /**     * @根据条件查找     * @param dictionaryData     * @return     */    @Override    public List<DictionaryData> selectByCondition(DictionaryData dictionaryData) {        return dictionaryDataMapper.selectByCondition(dictionaryData);    }    /**     * @根据字典名称修改     * @param list     * @return     */    @Override    public Integer updateListByDictdataName(List<DictionaryData> list) {        return dictionaryDataMapper.updateListByDictdataName(list);    }    /**     * @根据字典名称查询     * @param dictdataName     * @return     */    @Override    public String findValueByDictdataName(String dictdataName) {       return dictionaryDataMapper.findOneByDictdataName(dictdataName);    }}