package com.rongke.service.impl;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.GoodsMapper;import com.rongke.model.Goods;import com.rongke.service.GoodsService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;/** * @GoodsServiceImpl * @商品表 ServiceImpl * @version : Ver 1.0 */@Servicepublic class GoodsServiceImpl extends ServiceImpl<GoodsMapper, Goods> implements GoodsService {    @Autowired    private GoodsMapper goodsMapper;    /**     * @根据条件查找商品表      * @param goods     * @return     */    @Override    public List<Goods> selectByCondition(Goods goods) {        return goodsMapper.selectByCondition(goods);    }    @Override    public List<Goods> selectGoodsPage(Map map) {        return goodsMapper.selectGoodsPage(map);    }    @Override    public Integer selectGoodsPageSize(Map map) {        return goodsMapper.selectGoodsPageSize(map);    }}