package com.rongke.service.impl;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.HelpCenterMapper;import com.rongke.model.HelpCenter;import com.rongke.service.HelpCenterService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @HelpCenterServiceImpl * @ServiceImpl * @version : Ver 1.0 */@Servicepublic class HelpCenterServiceImpl extends ServiceImpl<HelpCenterMapper, HelpCenter> implements HelpCenterService {    @Autowired    private HelpCenterMapper helpCenterMapper;    /**     * @根据条件查找     * @param helpCenter     * @return     */    @Override    public List<HelpCenter> selectByCondition(HelpCenter helpCenter) {        return helpCenterMapper.selectByCondition(helpCenter);    }}