package com.rongke.service.impl;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.OrderSumMapper;import com.rongke.model.OrderSum;import com.rongke.service.OrderSumService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;/** * @OrderSumServiceImpl * @订单统计ServiceImpl * @version : Ver 1.0 */@Servicepublic class OrderSumServiceImpl extends ServiceImpl<OrderSumMapper, OrderSum> implements OrderSumService {    @Autowired    private OrderSumMapper orderSumMapper;    public List<OrderSum> selectListAlls(Map map){        return  orderSumMapper.selectListAlls(map);    }    public Integer selectListAllsCount(Map map){        return  orderSumMapper.selectListAllsCount(map);    };}