package com.rongke.service.impl;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.PhoneAddressBookMapper;import com.rongke.model.PhoneAddressBook;import com.rongke.service.PhoneAddressBookService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @PhoneAddressBookServiceImpl * @手机通讯录ServiceImpl * @version : Ver 1.0 */@Servicepublic class PhoneAddressBookServiceImpl extends ServiceImpl<PhoneAddressBookMapper, PhoneAddressBook> implements PhoneAddressBookService {    @Autowired    private PhoneAddressBookMapper phoneAddressBookMapper;    /**     * @根据条件查找手机通讯录     * @param phoneAddressBook     * @return     */    @Override    public List<PhoneAddressBook> selectByCondition(PhoneAddressBook phoneAddressBook) {        return phoneAddressBookMapper.selectByCondition(phoneAddressBook);    }}