package com.rongke.service.impl;import com.baomidou.mybatisplus.mapper.EntityWrapper;import com.baomidou.mybatisplus.plugins.Page;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.AddressMapper;import com.rongke.mapper.SysMaintainMapper;import com.rongke.model.Address;import com.rongke.model.SysMaintain;import com.rongke.service.AddressService;import com.rongke.service.SysMaintainService;import com.rongke.utils.ans.PageUtils;import com.rongke.utils.ans.Query;import org.apache.commons.lang3.StringUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;/** * @AddressServiceImpl * @地址ServiceImpl * @version : Ver 1.0 */@Servicepublic class SysMaintainServiceImpl extends ServiceImpl<SysMaintainMapper,SysMaintain> implements SysMaintainService{    public PageUtils sysMaintain(Map<String, Object> params) {        String time1 = (String) params.get("time1");        String time2 = (String) params.get("time2");        Page<SysMaintain> page = this.selectPage(new Query<SysMaintain>(params).getPage(), new EntityWrapper<SysMaintain>()                .between(StringUtils.isNotBlank(time1)&&StringUtils.isNotBlank(time2),"create_time",time1,time2)        );        return new PageUtils(page);    }}