package com.rongke.service.impl;import com.baomidou.mybatisplus.mapper.EntityWrapper;import com.baomidou.mybatisplus.mapper.SqlHelper;import com.baomidou.mybatisplus.mapper.Wrapper;import com.baomidou.mybatisplus.plugins.Page;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.TrackLogMapper;import com.rongke.model.TrackLog;import com.rongke.service.TrackLogService;import com.rongke.utils.ans.PageUtils;import com.rongke.utils.ans.Query;import org.apache.commons.lang3.StringUtils;import org.springframework.stereotype.Service;import java.util.Arrays;import java.util.Map;/** * 跟踪信息 */@Servicepublic class TrackLogServiceImpl extends ServiceImpl<TrackLogMapper, TrackLog> implements TrackLogService{    /**     *  跟踪信息     */    public PageUtils trackLogPage(Map<String, Object> params){        Page<Map<String, Object>> mapPage = trackLogPage(new Query<Map<String, Object>>(params).getPage(), new EntityWrapper<TrackLog>()                .eq(StringUtils.isNotBlank((String) params.get("userId")),"e.user_id",params.get("userId"))                .eq(StringUtils.isNotBlank((String)params.get("evaluationId")),"t.evaluation_id",params.get("evaluationId"))        );        return new PageUtils(mapPage);    }    public Page<Map<String,Object>> trackLogPage(Page<Map<String,Object>> val1,Wrapper<TrackLog> wrapper){        SqlHelper.fillWrapper(val1, wrapper);        val1.setRecords(this.baseMapper.TrackJoEvaJoOrderJoAdminJoUser(val1, wrapper));        return val1;    }}