package com.rongke.service.impl;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.UserBaseMsgMapper;import com.rongke.model.UserBaseMsg;import com.rongke.service.UserBaseMsgService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;/** * @UserBaseMsgServiceImpl * @用户基本信息ServiceImpl * @version : Ver 1.0 */@Servicepublic class UserBaseMsgServiceImpl extends ServiceImpl<UserBaseMsgMapper, UserBaseMsg> implements UserBaseMsgService {    @Autowired    private UserBaseMsgMapper userBaseMsgMapper;    /**     * @根据条件查找用户基本信息     * @param userBaseMsg     * @return     */    @Override    public List<UserBaseMsg> selectByCondition(UserBaseMsg userBaseMsg) {        return userBaseMsgMapper.selectByCondition(userBaseMsg);    }    @Override    public UserBaseMsg selectBaseMsg(Map map) {        return userBaseMsgMapper.selectBaseMsg(map);    }    public List<UserBaseMsg> selectByPage(Map<String,Object> map){        List<UserBaseMsg> userBasicMsgs = userBaseMsgMapper.selectByPage(map);        return userBasicMsgs;    }    public UserBaseMsg selectOneDetails(String id){        UserBaseMsg userBasicMsg = userBaseMsgMapper.selectOneDetails(id);        return userBasicMsg;    }    public Integer selectCount(Map<String,Object> map){        return userBaseMsgMapper.selectCount(map);    }    @Override    public List<Map> selectMapsByPage(Map<String, Object> map) {        return userBaseMsgMapper.selectMapsByPage(map);    }}