package com.rongke.service.impl;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.UserContactMapper;import com.rongke.model.UserContact;import com.rongke.service.UserContactService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @UserContactServiceImpl * @用户联系人ServiceImpl * @version : Ver 1.0 */@Servicepublic class UserContactServiceImpl extends ServiceImpl<UserContactMapper, UserContact> implements UserContactService {    @Autowired    private UserContactMapper userContactMapper;    /**     * @根据条件查找用户联系人     * @param userContact     * @return     */    @Override    public List<UserContact> selectByCondition(UserContact userContact) {        return userContactMapper.selectByCondition(userContact);    }}