package com.rongke.service.impl;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.UserCouponMapper;import com.rongke.model.UserCoupon;import com.rongke.service.UserCouponService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;/** * @UserCouponServiceImpl * @用户优惠券列ServiceImpl * @version : Ver 1.0 */@Servicepublic class UserCouponServiceImpl extends ServiceImpl<UserCouponMapper, UserCoupon> implements UserCouponService {    @Autowired    private UserCouponMapper userCouponMapper;    /**     * @根据条件查找用户优惠券列     * @param userCoupon     * @return     */    @Override    public List<UserCoupon> selectByCondition(UserCoupon userCoupon) {        return userCouponMapper.selectByCondition(userCoupon);    }    @Override    public List<UserCoupon> selectUserCoupons(Map map) {        return userCouponMapper.selectUserCoupons(map);    }}