package com.rongke.service.impl;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.UserIdentityMapper;import com.rongke.model.UserIdentity;import com.rongke.service.UserIdentityService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;/** * @UserIdentityServiceImpl * @身份认证ServiceImpl * @version : Ver 1.0 */@Servicepublic class UserIdentityServiceImpl extends ServiceImpl<UserIdentityMapper, UserIdentity> implements UserIdentityService {    @Autowired    private UserIdentityMapper userIdentityMapper;    /**     * @根据条件查找身份认证     * @param userIdentity     * @return     */    @Override    public List<UserIdentity> selectByCondition(UserIdentity userIdentity) {        return userIdentityMapper.selectByCondition(userIdentity);    }    @Override    public UserIdentity selectUserInentityData(Map map) {        return userIdentityMapper.selectUserInentityData(map);    }    @Override    public List<UserIdentity> selectByPage(Map<String,Object> map){        List<UserIdentity> list = userIdentityMapper.selectByPage(map);        return list;    }    @Override    public Integer selectCount(Map<String,Object> map){        Integer count = userIdentityMapper.selectCount(map);        return count;    }    @Override    public Integer selectPartCount(Map<String, Object> map) {        return userIdentityMapper.selectPartCount(map);    }    @Override    public List<UserIdentity> selectPartByPage(Map<String, Object> map) {        return userIdentityMapper.selectPartByPage(map);    }}