package com.rongke.service.impl;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.UserMessageMapper;import com.rongke.model.UserMessage;import com.rongke.service.UserMessageService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @UserMessageServiceImpl * @用户消息ServiceImpl * @version : Ver 1.0 */@Servicepublic class UserMessageServiceImpl extends ServiceImpl<UserMessageMapper, UserMessage> implements UserMessageService {    @Autowired    private UserMessageMapper userMessageMapper;    /**     * @根据条件查找用户消息     * @param userMessage     * @return     */    @Override    public List<UserMessage> selectByCondition(UserMessage userMessage) {        return userMessageMapper.selectByCondition(userMessage);    }}