package com.rongke.service.impl;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.UserPhoneMapper;import com.rongke.model.UserPhone;import com.rongke.service.UserPhoneService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;/** * @UserPhoneServiceImpl * @ServiceImpl * @version : Ver 1.0 */@Servicepublic class UserPhoneServiceImpl extends ServiceImpl<UserPhoneMapper, UserPhone> implements UserPhoneService {    @Autowired    private UserPhoneMapper userPhoneMapper;    /**     * @根据条件查找     * @param userPhone     * @return     */    @Override    public List<UserPhone> selectByCondition(UserPhone userPhone) {        return userPhoneMapper.selectByCondition(userPhone);    }    public List<UserPhone> selectByPage(Map<String,Object> map){       return userPhoneMapper.selectByPage(map);    }    public Integer selectCount(Map<String,Object> map){return userPhoneMapper.selectCount(map);}    @Override    public Integer selectPartCount(Map<String, Object> map) {        return userPhoneMapper.selectPartCount(map);    }    @Override    public List<UserPhone> selectPartByPage(Map<String, Object> map) {        return userPhoneMapper.selectPartByPage(map);    }}