package com.rongke.service.impl;import com.baomidou.mybatisplus.mapper.EntityWrapper;import com.baomidou.mybatisplus.mapper.SqlHelper;import com.baomidou.mybatisplus.mapper.Wrapper;import com.baomidou.mybatisplus.plugins.Page;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.VersionMapper;import com.rongke.model.PushMsg;import com.rongke.model.Version;import com.rongke.service.VersionService;import com.rongke.utils.ans.PageUtils;import com.rongke.utils.ans.Query;import org.apache.commons.lang3.StringUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;/** * @VersionServiceImpl * @ServiceImpl * @version : Ver 1.0 */@Servicepublic class VersionServiceImpl extends ServiceImpl<VersionMapper, Version> implements VersionService {    /**     * @根据条件查找     * @param version     * @return     */    @Override    public List<Version> selectByCondition(Version version) {        return baseMapper.selectByCondition(version);    }    public PageUtils selectAllVersionList(Map<String, Object> params) {        Page<Map<String, Object>> page = versionList(new Query<Map<String, Object>>(params).getPage(),new EntityWrapper<Version>()                .eq(StringUtils.isNotBlank((String)params.get("type")),"type", params.get("type"))        );        return new PageUtils(page);    }    /**     * 自定义mybatis-plus sql查询     */    public Page<Map<String,Object>> versionList(Page<Map<String,Object>> page, Wrapper<Version> wrapper) {        SqlHelper.fillWrapper(page, wrapper);        page.setRecords(this.baseMapper.versionList(page, wrapper));        return page;    }}