package com.rongke.service.impl.ans.sys;import com.rongke.mapper.ans.TblAccountDao;import com.rongke.model.ans.TblAccountEntity;import com.rongke.service.ans.sys.TblAccountService;import com.rongke.utils.ans.PageUtils;import com.rongke.utils.ans.Query;import org.apache.commons.lang.StringUtils;import org.springframework.stereotype.Service;import java.util.Map;import com.baomidou.mybatisplus.mapper.EntityWrapper;import com.baomidou.mybatisplus.plugins.Page;import com.baomidou.mybatisplus.service.impl.ServiceImpl;@Service("tblAccountService")public class TblAccountServiceImpl extends ServiceImpl<TblAccountDao, TblAccountEntity> implements TblAccountService {    @Override    public PageUtils queryPage(Map<String, Object> params) {        Page<TblAccountEntity> page = this.selectPage(                new Query<TblAccountEntity>(params).getPage(),                new EntityWrapper<TblAccountEntity>()                .like(StringUtils.isNotBlank((String)params.get("accountName")),"Account_Name",(String)params.get("accountName"))        );        return new PageUtils(page);    }}