package com.rongke.service.impl.ans.sys;import com.rongke.mapper.ans.UserDebitDao;import com.rongke.model.ans.UserDebitEntity;import com.rongke.service.ans.sys.UserDebitService;import com.rongke.utils.ans.MapUtils;import com.rongke.utils.ans.PageUtils;import com.rongke.utils.ans.Query;import org.apache.commons.lang3.StringUtils;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;import com.baomidou.mybatisplus.mapper.EntityWrapper;import com.baomidou.mybatisplus.plugins.Page;import com.baomidou.mybatisplus.service.impl.ServiceImpl;@Service("userDebitService")public class UserDebitServiceImpl extends ServiceImpl<UserDebitDao, UserDebitEntity> implements UserDebitService {    public PageUtils queryPage(Map<String, Object> params) {        Page<UserDebitEntity> page = this.selectPage(                new Query<UserDebitEntity>(params).getPage(),                new EntityWrapper<UserDebitEntity>()                        .eq(StringUtils.isNotBlank((String)params.get("name")),"WeChatUser_Name",params.get("name"))                        .eq(StringUtils.isNotBlank((String)params.get("type")),"type",params.get("type"))        );        return new PageUtils(page);    }    /**     * 获得总 7 30 累计应得金额     * @param params     * @return     */    public List<String> StillANDCollectMoney(Map<String, Object> params) {        return baseMapper.daydebitZSS(params);    }    public List<String> daydebitZHC(String accountID,String type) {        return this.baseMapper.daydebitZHC(new MapUtils().put("accountID",accountID).put("type",type));    }    @Override    public List<String> tjDebit() {        return this.baseMapper.tjDebit();    }}