package com.rongke.service.impl.ans.sys;import com.baomidou.mybatisplus.mapper.EntityWrapper;import com.baomidou.mybatisplus.plugins.Page;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.ans.WxAboutDao;import com.rongke.model.ans.WxAboutEntity;import com.rongke.service.ans.sys.WxAboutService;import com.rongke.utils.ans.PageUtils;import com.rongke.utils.ans.Query;import org.apache.commons.lang3.StringUtils;import org.springframework.stereotype.Service;import java.util.Arrays;import java.util.Map;@Service("wxAboutService")public class WxAboutServiceImpl extends ServiceImpl<WxAboutDao, WxAboutEntity> implements WxAboutService {    @Override    public PageUtils queryPage(Map<String, Object> params) {        Page<WxAboutEntity> page = this.selectPage(                new Query<WxAboutEntity>(params).getPage(),                new EntityWrapper<WxAboutEntity>()                        .setSqlSelect("id,title,create_time as createTime,update_time as updateTime,status")                        .like(StringUtils.isNotBlank((String)params.get("title")),"title", (String) params.get("title"))                        .eq(StringUtils.isNotBlank((String)params.get("status")),"status",params.get("status"))                        .orderDesc(Arrays.asList("create_time"))        );        return new PageUtils(page);    }}