package com.rongke.service.impl.ans.sys;import com.baomidou.mybatisplus.mapper.EntityWrapper;import com.baomidou.mybatisplus.plugins.Page;import com.baomidou.mybatisplus.service.impl.ServiceImpl;import com.rongke.mapper.ans.WxRecordDao;import com.rongke.model.ans.WxRecordEntity;import com.rongke.service.ans.sys.WxRecordService;import com.rongke.utils.ans.PageUtils;import com.rongke.utils.ans.Query;import org.springframework.stereotype.Service;import java.util.Map;@Service("wxRecordService")public class WxRecordServiceImpl extends ServiceImpl<WxRecordDao, WxRecordEntity> implements WxRecordService {    @Override    public PageUtils queryPage(Map<String, Object> params) {        Page<WxRecordEntity> page = this.selectPage(                new Query<WxRecordEntity>(params).getPage(),                new EntityWrapper<WxRecordEntity>()        );        return new PageUtils(page);    }}