package com.rongke.web;import com.rongke.commons.JsonResp;import com.rongke.mapper.DictionaryMapper;import com.rongke.model.Dictionary;import com.rongke.service.DictionaryService;import org.apache.log4j.Logger;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.transaction.annotation.Transactional;import org.springframework.web.bind.annotation.*;import java.math.BigInteger;import java.util.Date;import java.util.List;/** * @DictionaryController * @Controller * @version : Ver 1.0 */@RestController@RequestMapping(value="/api/dictionary")@Transactional@CrossOriginpublic class DictionaryController {    private Logger log = Logger.getLogger(this.getClass());    @Autowired    private DictionaryService dictionaryService;    /**     * @添加     * @param dictionary     * @return 返回值JsonResp     */    @RequestMapping(value="/add", method = RequestMethod.POST)    public JsonResp addDictionary(@RequestBody Dictionary dictionary){        log.debug("添加");        dictionaryService.insert(dictionary);        return JsonResp.ok(dictionary);    }    /**     * @修改     * @param dictionary     * @return 返回值JsonResp     */    @RequestMapping(value="/update", method = RequestMethod.POST)    public JsonResp updateDictionary(@RequestBody Dictionary dictionary){        log.debug("修改");        dictionaryService.updateById(dictionary);        return JsonResp.ok(dictionary);    }    /**     * @根据id查找     * @param id     * @return 返回值JsonResp     */    @RequestMapping(value="/selectOne", method = RequestMethod.GET)    public JsonResp selectDictionary(Long id){        log.debug("查找");        Dictionary dictionary = dictionaryService.selectById(id);        return JsonResp.ok(dictionary);    }    /**     * @根据条件查找     * @param dictionary     * @return 返回值JsonResp     */    @RequestMapping(value = "/selectByCondition", method = RequestMethod.POST)    public JsonResp selectDictionaryByCondition(@RequestBody Dictionary dictionary){        log.debug("根据条件查找");        if(dictionary==null)            return JsonResp.toFail("参数不能为空");        List<Dictionary> dictionaryList=dictionaryService.selectByCondition(dictionary);        return JsonResp.ok(dictionaryList);    }}