package com.rongke.web;import com.baomidou.mybatisplus.mapper.EntityWrapper;import com.rongke.commons.JsonResp;import com.rongke.model.Address;import com.rongke.model.SysMaintain;import com.rongke.service.AddressService;import com.rongke.service.SysMaintainService;import com.rongke.utils.ans.R;import org.apache.log4j.Logger;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.transaction.annotation.Transactional;import org.springframework.web.bind.annotation.*;import java.util.Date;import java.util.List;/** * @AddressController * @地址Controller * @version : Ver 1.0 */@RestController@RequestMapping(value="/api/sysMaintain")@Transactional@CrossOriginpublic class SysMaintainController {    private Logger log = Logger.getLogger(this.getClass());    @Autowired    private SysMaintainService sysMaintainService;    @RequestMapping("/maintain")    public R maintain(){        SysMaintain online = sysMaintainService.selectOne(new EntityWrapper<SysMaintain>().eq("online", "1"));        return R.ok().put("data",online);    }}