package com.rongke.web.ans.utils;import java.math.BigDecimal;import java.text.SimpleDateFormat;import java.util.Date;public class DebitUtils {    /**     * 获取当前时间加四位随机数     */    public static String DatetoStringGetSymbol() {        Date currentTime = new Date();        SimpleDateFormat formatter = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss");        Integer integer = (int) ((Math.random() * (9999 - 1000 + 1)) + 1000);        String dateString = formatter.format(currentTime) + String.valueOf(integer);        String response = dateString.replaceAll("[[\\s-:punct:.]]", "");        return response;    }}