package com.rongke.web.ans.utils;import com.alibaba.fastjson.JSON;import com.alibaba.fastjson.JSONObject;import com.rongke.utils.HttpClientUtil;public class RSTcreditAPI {    static final String tcUrl = "http://47.111.9.78:3335/tianchuang_v1/";    static final String tcToken = "50dec4d4aec13fa549ad36173281d7e4";    public static JSONObject verifyIdcard(String name, String mobile, String idCard) {        String url = tcUrl + "verifyIdcard";        String body = "name=" + name +                "&mobile=" + mobile +                "&idCard=" + idCard +                "&passbackParams=" + tcToken;        String result = HttpClientUtil.sendPostSSLRequest(url, body, "UTF-8", "application/x-www-form-urlencoded");        JSONObject json = JSON.parseObject(result);        return json;    }}