package com.rongke.web.ans.vo;import com.baomidou.mybatisplus.annotations.TableField;import com.fasterxml.jackson.annotation.JsonFormat;import com.fasterxml.jackson.annotation.JsonInclude;import com.rongke.utils.DateUtils;import java.io.Serializable;import java.math.BigDecimal;import java.util.Date;import java.util.Map;//空不返回@JsonInclude(JsonInclude.Include.NON_NULL)public class DebitVO implements Serializable {    private static final long serialVersionUID = 5699715478747775238L;    /**     * 借条ID     */    private Long id;    /**     * 利息 %     */    private String interest;    /**     * 借款日期     */    @JsonFormat(pattern = "yyyy-MM-dd",timezone = "GMT+8")    private Date startTime;    /**     * 还款日期     */    @JsonFormat(pattern = "yyyy-MM-dd",timezone = "GMT+8")    private Date endTime;    /**     * 借款时长     */    private Long Lengthoftime;    /**     * 借条状态  1 待确认 2 已同意 3 已驳回 4 已完结     */    private Integer debitstatus;    /**     * 最后还款时间     */    private Date surplusTime;    /**     * 借款金额     */    private BigDecimal money;    /**     * 剩余金额     */    private BigDecimal surplusMoney;    /**     * 逾期天数     */    private String BODay;    /**     * 剩余天数     */    private String surplusDay;    /**     * 应该还金额     */    private BigDecimal huankuan;    /**     * 订单ID     */    private String orderId;    /**     * 借款人 出款人信息     */    private Map<String,Object> from;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getInterest() {        return interest;    }    public void setInterest(String interest) {        this.interest = interest;    }    public Date getStartTime() {        return startTime;    }    public void setStartTime(Date startTime) {        this.startTime = startTime;    }    public Date getEndTime() {        return endTime;    }    public void setEndTime(Date endTime) {        this.endTime = endTime;    }    public Long getLengthoftime() {        if(this.startTime!=null&&this.endTime!=null){            return DateUtils.DaysubDay(DateUtils.dateSimpleSFM(this.startTime),DateUtils.dateSimpleSFM(this.endTime));        }        return Lengthoftime;    }    public void setLengthoftime(Long lengthoftime) {        Lengthoftime = lengthoftime;    }    public Integer getDebitstatus() {        return debitstatus;    }    public void setDebitstatus(Integer debitstatus) {        this.debitstatus = debitstatus;    }    public Date getSurplusTime() {        return surplusTime;    }    public void setSurplusTime(Date surplusTime) {        this.surplusTime = surplusTime;    }    public BigDecimal getMoney() {        return money;    }    public void setMoney(BigDecimal money) {        this.money = money;    }    public BigDecimal getSurplusMoney() {        return surplusMoney;    }    public void setSurplusMoney(BigDecimal surplusMoney) {        this.surplusMoney = surplusMoney;    }    /**     * 获得:逾期时间     */    public String getBODay() {        if(this.endTime==null){            return "还款时间未知";        }        String day = DateUtils.DaysubDay(DateUtils.dateSimpleSFM(new Date()),DateUtils.dateSimpleSFM(this.endTime)).toString();        if(Integer.valueOf(day)>0){            return "未逾期";        }        return day.substring(1)+"天";    }    public void setBODay(String BODay) {        this.BODay = BODay;    }    public BigDecimal getHuankuan() {        if(this.surplusMoney!=null && this.getEndTime()!=null){            Long day = DateUtils.DatasubDataRDAYJ(DateUtils.dateSimpleSFM(this.getEndTime()), DateUtils.nowDate());            if(day==0){                return this.surplusMoney;            }            BigDecimal h = this.surplusMoney.multiply(new BigDecimal(this.interest).divide(new BigDecimal(100))).multiply(new BigDecimal(day)).add(this.surplusMoney);//要还金额            return h;        }        return huankuan;    }    public void setHuankuan(BigDecimal huankuan) {        this.huankuan = huankuan;    }    /**     * 获得：剩余天数     */    public String getSurplusDay() {        return surplusDay;    }    public void setSurplusDay(String surplusDay) {        this.surplusDay = surplusDay;    }    public Map<String, Object> getFrom() {        return from;    }    public void setFrom(Map<String, Object> from) {        this.from = from;    }    /**     * 获取：订单ID     */    public String getOrderId() {        return orderId;    }    /**     * 设置：订单ID     */    public void setOrderId(String orderId) {        this.orderId = orderId;    }}