package com.rongke.web.yibao;import net.sf.json.JSONArray;import net.sf.json.JSONObject;import java.io.BufferedReader;import java.io.FileInputStream;import java.io.IOException;import java.io.InputStreamReader;import java.net.URL;import java.util.Iterator;/** * 读取json文件获取密钥 */public class Test {    public static  String getvalue(String key,String filePath) throws IOException {        String value=null;        URL url=Test.class.getClassLoader().getResources("config/yop_sdk_config_default.json").nextElement();        String JsonContext=ReadFile(url.getPath());        JSONObject jsonobject= JSONObject.fromObject(JsonContext);        JSONArray jsonarray=jsonobject.getJSONArray(key);        Iterator iteratorResult = jsonarray.iterator();        while (iteratorResult.hasNext()) {            JSONObject iteratorJson = (JSONObject) iteratorResult.next();            value=iteratorJson.getString("value");            System.out.println("商户私钥："+value);        }        return value;    }    public static  String ReadFile(String Path){        BufferedReader reader = null;        String laststr = "";        try{            FileInputStream fileInputStream = new FileInputStream(Path);            InputStreamReader inputStreamReader = new InputStreamReader(fileInputStream);            reader = new BufferedReader(inputStreamReader);            String tempString = null;            while((tempString = reader.readLine()) != null){                laststr += tempString;            }            reader.close();        }catch(Exception e){            e.printStackTrace();        }finally{            if(reader != null){                try {                    reader.close();                } catch (Exception e) {                    e.printStackTrace();                }            }        }        return laststr;    }}